import React, { useEffect, useState } from "react";

import { Heading, Container, SubHeading, Button, Span } from "./styles";
import { useParams, useHistory } from "react-router-dom";
import Typist from "react-typist";
const PersonBio = () => {
  const { id }: any = useParams();
  const history = useHistory();

  interface Person {
    name: string;
    gender: string;
  }

  const [personInfo, setPersonInfo] = useState<Person[]>([
    {
      name: "",
      gender: "",
    },
  ]);

  useEffect(() => {
    fetch(`https://swapi.dev/api/people/${id}`)
      .then((res) => res.json())
      .then((data) =>
        setPersonInfo([
          {
            name: data.name,
            gender: data.gender,
          },
        ])
      );
  }, []);
  const goBack = () => {
    history.goBack();
  };

  return (
    <div>
      <Heading>
        PersonBio : <Span> {id} </Span>
      </Heading>

      <Container>
        <Typist>
          <SubHeading>
            <Typist.Delay ms={500} /> Name : {this.name}
          </SubHeading>
        </Typist>

        <Typist>
          <SubHeading>
            {" "}
            <Typist.Delay ms={800} />
            Gender : {personInfo.gender}
          </SubHeading>
        </Typist>

        <Typist>
          <SubHeading>
            <Typist.Delay ms={900} /> Mass :
          </SubHeading>
        </Typist>
        <Typist>
          <SubHeading>
            <Typist.Delay ms={1000} /> Height :
          </SubHeading>
        </Typist>

        <Typist>
          <SubHeading>
            <Typist.Delay ms={1200} /> Homeworld :
          </SubHeading>
        </Typist>

        <Button onClick={goBack}> Back</Button>
      </Container>
    </div>
  );
};

export default PersonBio;
