import styled, { keyframes } from "styled-components";

export const Heading = styled.h2`
  text-align: center;
  padding: 30px;
  color: white;
  font-weight: 300;
`;

export const Container = styled.div`
  display: flex;
  height: 100%;
  width: 80vw;
  position: absolute;
  justify-content: center;
  margin: 0 auto;
  right: 0;
  left: 0;
  flex-wrap: wrap;
`;

export const Card = styled.div`
  height: 200px;
  width: 200px;
  margin: 20px;
  padding: 20px;
  position: relative;
  cursor: pointer;
`;

export const CardBody = styled.div`
  color: white;
  position: absolute;
  height: 70%;
  width: 100%;
  display: flex;
  justify-content: center;
  align-items: center;
  font-weight: 100;
  font-size: 0.7rem;
  top: 0;
  left: 0;
  right: 0;
  margin: auto;
  animation: ${borders} 2s 1s infinite ease-in-out;
`;
