import styled, { keyframes } from "styled-components";

export const Heading = styled.h2`
  text-align: center;
  padding: 30px;
  color: red;
  font-weight: 300;
`;

export const Container = styled.div`
  border: 1px solid red;
  width: 50vw;
  margin: 0 25%;
`;

export const SubHeading = styled.h3`
  color: gold;
`;
