import styled from "styled-components";

export const Heading = styled.h2`
  text-align: center;
  padding: 30px;
  color: white;
  font-weight: 300;
`;

export const Container = styled.div`
  display: flex;
  height: 100%;
  width: 80vw;
  position: absolute;
  justify-content: center;
  margin: 0 auto;
  right: 0;
  left: 0;
  flex-wrap: wrap;
`;

export const Card = styled.div`
  height: 200px;
  width: 200px;
  margin: 20px;
  padding: 20px;
  position: relative;
  cursor: pointer;
`;
