import React from "react";
import { BrowserRouter as Router } from "react-router-dom";
import gql from "graphql-tag";
import { Query } from "react-apollo";
import {
  Card,
  CardBody,
  CharacterName,
  CardFooter,
  CardDetailsLink,
} from "./styles";

const PEOPLE_QUERY = gql`
  query PeopleQuery {
    people {
      name
      gender
      mass
      height
      homeworld
    }
  }
`;

const Character = () => {
  return {
    /*
      <Card>
      <CardBody>
      <CharacterName>Name</CharacterName>
      </CardBody>
      <CardFooter>
      <Router>
      <CardDetailsLink className="link" to="/">
      Details
      </CardDetailsLink>
        </Router>
      </CardFooter>
      </Card>
    */
  };
};

export default Character;
