import React, { Component } from "react";
import { BrowserRouter as Router } from "react-router-dom";
import gql from "graphql-tag";
import { Query } from "react-apollo";
import {
  Card,
  CardBody,
  CharacterName,
  CardFooter,
  CardDetailsLink,
} from "./styles";
import { render } from "react-dom";

const PEOPLE_QUERY = gql`
  query PeopleQuery {
    people {
      name
      gender
      mass
      height
      homeworld
    }
  }
`;

export class Character extends Component {
  render() {
    return (
      <div>
        <h1>People</h1>
        <Query query={PEOPLE_QUERY}></Query>
      </div>
      /*
                  <Card>
                  <CardBody>
                  <CharacterName>Name</CharacterName>
                  </CardBody>
                  <CardFooter>
                  <Router>
                  <CardDetailsLink className="link" to="/">
                  Details
                  </CardDetailsLink>
                    </Router>
                  </CardFooter>
                  </Card>
                */
    );
  }
}

export default Character;
