import React, { useEffect, useState } from "react";

import { BrowserRouter as Router, Route } from "react-router-dom";
import {
  Card,
  CardBody,
  CharacterName,
  CardFooter,
  CardDetailsLink,
} from "./styles";

import PersonBio from "../PersonBio/PersonBio";
import { PeopleBio } from "../..";

const PeopleCard = ({
  person: { name, url, mass, height, gender, homeworld },
}) => {
  //Get Single URL and Pass as props
  const [personInfo, setPersonInfo] = useState([]);

  useEffect(() => {
    fetch(url)
      .then((res) => res.json())
      .then((data) => {
        setPersonInfo(data.url);
        console.log(`Person : ${getId(url)}`, data);
      });
  }, []);

  <PeopleBio personInfo={personInfo} />;
  //Get ID from URL so to identify each person
  function getId(url) {
    return url.split("/")[url.split("/").length - 2];
  }

  return (
    <div>
      <Card>
        <CardBody>
          <CharacterName>{name}</CharacterName>
        </CardBody>
        <CardFooter>
          <Router>
            <CardDetailsLink className="link" to={`/details/${getId(url)}`}>
              Details
            </CardDetailsLink>
          </Router>
        </CardFooter>
      </Card>
    </div>
  );
};

export default PeopleCard;
